#!/bin/python

from operator import mul

def get_products(num):
	
	products = []
	array = [int(num[i]) for i in range(len(num))]
	substrings = [array[i-5:i] for i in range(len(array), 5, -1)]
	
	for substring in substrings:
		products.append(reduce(mul, substring))
		
	return max(products)
		

